import { useState } from 'react';

const App = () => {
  const [form, setForm] = useState({
    name: ''
  });

  const handleChange = ({target}) => {
    setForm({
      ...form,
      [target.name]: target.value
    });
  }

console.log(form)

  return (
    <form>
      <input 
        name='name'
        placeholder='Name'
        value={form.name}
        onChange={handleChange} />
    </form>
  );
}

export default App;
