const useForm = (initial) => {
  const [form, setForm] = useForm(initial);

  const handleChange = ({target}) => {
    setForm({
      ...form,
      [target.name]: target.type === 'checkbox' ?
        target.checked
        : target.name
    });
  }

  return [form, handleChange];
}

export default useForm;
